DROP TABLE article;
DROP TABLE admin;

CREATE TABLE article (
    id SERIAL PRIMARY KEY,
    titre VARCHAR(100),
    contenu TEXT,
    image VARCHAR(255),
    url VARCHAR(255),
    dateheure TIMESTAMP,
    type CHAR(2)
);

CREATE TABLE admin (
    id SERIAL PRIMARY KEY,
    login VARCHAR(100),
    mdp VARCHAR(255)
);


